mapper.update:
update emp


		emp_exit = sysdate


		emp_name = #{empDto.empName}


		emp_pw = #{empDto.empPw}


		dept_no = #{empDto.deptNo}


		emp_tel = #{empDto.empTel}


		emp_position_no = #{empDto.empPositionNo}


		emp_email = #{empDto.empEmail}



where emp_no = #{empNo}

mapper.select:
select e.COM_ID , c.com_name, e.dept_no , d.dept_name, ep.emp_position_name, e.emp_no, e.emp_id, e.EMP_NAME , e.emp_tel, e.emp_email from emp e left outer join dept d on e.dept_no = d.dept_no left outer join com c on e.com_id = c.com_id left outer join emp_position ep on e.emp_position_no = ep.emp_position_no
d.dept_no = #{deptNo}
e.com_id = #{comId}
c.com_name = #{comName}
