mapper.update:
update receivers 


		receivers_no = #{dto.receiversNo},


		receivers_status ='Y',	

	 RECEIVERS_CONFIRM_TIME = SYSDATE ,

	  receivers_return_rs= #{receiversReturnRs} 


where path_no= #{pathNo} and receivers_receiver = #{receiversReceiver}

update receivers 


		receivers_no = #{dto.receiversNo},


		receivers_status ='N',	

	 RECEIVERS_CONFIRM_TIME = SYSDATE ,

	  receivers_return_rs= #{receiversReturnRs} 


where path_no= #{pathNo} and receivers_receiver = #{receiversReceiver}
